import { ChainId, CurrencyAmount, ETHER, Percent, Token, TokenAmount, TradeType, WETH9 } from '@uniswap/sdk-core'
import { FeeAmount, TICK_SPACINGS } from './constants'
import { Pool } from './entities/pool'
import { SwapRouter } from './swapRouter'
import { nearestUsableTick, TickMath } from './utils'
import { encodeSqrtRatioX96 } from './utils/encodeSqrtRatioX96'
import { Route, Trade } from './entities'
import JSBI from 'jsbi'

describe('SwapRouter', () => {
  const token0 = new Token(ChainId.MAINNET, '0x0000000000000000000000000000000000000001', 18, 't0', 'token0')
  const token1 = new Token(ChainId.MAINNET, '0x0000000000000000000000000000000000000002', 18, 't1', 'token1')

  const feeAmount = FeeAmount.MEDIUM
  const sqrtRatioX96 = encodeSqrtRatioX96(1, 1)
  const liquidity = 1_000_000

  const pool_0_1 = new Pool(
    token0,
    token1,
    feeAmount,
    sqrtRatioX96,
    liquidity,
    TickMath.getTickAtSqrtRatio(sqrtRatioX96),
    [
      {
        index: nearestUsableTick(TickMath.MIN_TICK, TICK_SPACINGS[feeAmount]),
        liquidityNet: liquidity,
        liquidityGross: liquidity
      },
      {
        index: nearestUsableTick(TickMath.MAX_TICK, TICK_SPACINGS[feeAmount]),
        liquidityNet: -liquidity,
        liquidityGross: liquidity
      }
    ]
  )
  const WETH = WETH9[ChainId.MAINNET]
  const pool_1_weth = new Pool(
    token1,
    WETH,
    feeAmount,
    sqrtRatioX96,
    liquidity,
    TickMath.getTickAtSqrtRatio(sqrtRatioX96),
    [
      {
        index: nearestUsableTick(TickMath.MIN_TICK, TICK_SPACINGS[feeAmount]),
        liquidityNet: liquidity,
        liquidityGross: liquidity
      },
      {
        index: nearestUsableTick(TickMath.MAX_TICK, TICK_SPACINGS[feeAmount]),
        liquidityNet: -liquidity,
        liquidityGross: liquidity
      }
    ]
  )

  const slippageTolerance = new Percent(1, 100)
  const recipient = '0x0000000000000000000000000000000000000003'
  const deadline = 123

  describe('#swapCallParameters', () => {
    it('single-hop exact input', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_0_1], token0),
        new TokenAmount(token0, 100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('single-hop exact output', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_0_1], token0),
        new TokenAmount(token1, 100),
        TradeType.EXACT_OUTPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('multi-hop exact input', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_0_1, pool_1_weth], token0),
        new TokenAmount(token0, 100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('multi-hop exact output', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_0_1, pool_1_weth], token0),
        new TokenAmount(WETH, 100),
        TradeType.EXACT_OUTPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('ETH in exact input', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_1_weth], ETHER),
        CurrencyAmount.ether(100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x64')
    })

    it('ETH in exact output', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_1_weth], ETHER),
        new TokenAmount(token1, 100),
        TradeType.EXACT_OUTPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x67')
    })

    it('ETH out exact input', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_1_weth], token1, ETHER),
        new TokenAmount(token1, 100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('ETH out exact output', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_1_weth], token1, ETHER),
        CurrencyAmount.ether(100),
        TradeType.EXACT_OUTPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('sqrtPriceLimitX96', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_0_1], token0),
        new TokenAmount(token0, 100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline,
        sqrtPriceLimitX96: JSBI.exponentiate(JSBI.BigInt(2), JSBI.BigInt(128))
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })

    it('fee with eth out', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_1_weth], token1, ETHER),
        new TokenAmount(token1, 100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline,
        fee: {
          fee: new Percent(5, 1000),
          recipient
        }
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })
    it('fee', async () => {
      const trade = await Trade.fromRoute(
        new Route([pool_0_1], token0),
        new TokenAmount(token0, 100),
        TradeType.EXACT_INPUT
      )
      const { calldata, value } = SwapRouter.swapCallParameters(trade, {
        slippageTolerance,
        recipient,
        deadline,
        fee: {
          fee: new Percent(5, 1000),
          recipient
        }
      })

      expect(calldata).toBe(
        ''
      )
      expect(value).toBe('0x00')
    })
  })
})
